import { useEffect, useState } from "react";
import supabase from "./supabase";

import "./style.css";

const initialArt = [
  {
    id: 1,
    text: "This is my latte art. I made a flower.",
    recipe: "https://openrecipe.fb.com/",
    category: "espresso",
    votesMug: 24,
    votesHeart: 9,
    votesMindBlown: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "I SUCK at latte art.",
    recipe:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "lattes",
    votesMug: 17,
    votesHeart: 9,
    votesMindBlown: 1,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Cortados are my favorite milk drink.",
    recipe: "https://en.wikipedia.org/wiki/Lisbon",
    category: "cortados",
    votesMug: 8,
    votesHeart: 2,
    votesMindBlown: 1,
    createdIn: 2015,
  },
];

function Counter() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <span style={{ fontSize: "40px" }}>{count}</span>
      <button className="btn btn-large" onClick={() => setCount((c) => c + 1)}>
        +1
      </button>
    </div>
  );
}

function App() {
  const [showForm, setShowForm] = useState(false);
  const [art, setArt] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentCategory, setCurrentCategory] = useState("all");

  useEffect(
    function () {
      async function getArt() {
        setIsLoading(true);

        let query = supabase.from("art").select("*");

        if (currentCategory !== "all")
          query = query.eq("category", currentCategory);

        const { data: updatedArt, error } = await query
          .order("votesMug", { ascending: false })
          .limit(1000);

        if (!error) setArt(art);
        else alert("There was a problem getting data");
        setIsLoading(false);
      }
      getArt();
    },
    [currentCategory]
  );

  return (
    <>
      <Header showForm={showForm} setShowForm={setShowForm} />
      {showForm ? (
        <NewArtForm setArt={setArt} setShowForm={setShowForm} />
      ) : null}

      <main className="main">
        <CategoryFilter setCurrentCategory={setCurrentCategory} />

        {isLoading ? <Loader /> : <ArtList art={art} setArt={setArt} />}
      </main>
    </>
  );
}

function Loader() {
  return <p className="message">Loading...</p>;
}

function Header({ showForm, setShowForm }) {
  const appTitle = "Today I Learned";

  return (
    <header className="header">
      <div className="logo">
        <img src="logo.png" height="68" width="68" alt="Today I Learned Logo" />
        <h1>{appTitle}</h1>
      </div>

      <button
        className="btn btn-large btn-open"
        onClick={() => setShowForm((show) => !show)}
      >
        {showForm ? "Close" : "Share a art"}
      </button>
    </header>
  );
}

const CATEGORIES = [
  { name: "espresso", color: "#3b82f6" },
  { name: "lattes", color: "#16a34a" },
  { name: "cortados", color: "#ef4444" },
  { name: "cappucinos", color: "#eab308" },
];

function isValidHttpUrl(string) {
  let url;
  try {
    url = new URL(string);
  } catch (_) {
    return false;
  }
  return url.protocol === "http:" || url.protocol === "https:";
}

function NewArtForm({ setArt, setShowForm }) {
  const [text, setText] = useState("");
  // Fixed in a video text overlay
  const [recipe, setRecipe] = useState("");
  const [category, setCategory] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const textLength = text.length;

  async function handleSubmit(e) {
    // 1. Prevent browser reload
    e.preventDefault();
    console.log(text, recipe, category);

    // 2. Check if data is valid. If so, create a new art
    if (text && isValidHttpUrl(recipe) && category && textLength <= 200) {
      // 3. Create a new art object
      // const newart = {
      //   id: Math.round(Math.random() * 10000000),
      //   text,
      //   recipe,
      //   category,
      //   votesInteresting: 0,
      //   votesMindblowing: 0,
      //   votesFalse: 0,
      //   createdIn: new Date().getFullYear(),
      // };

      // 3. Upload art to Supabase and receive the new art object
      setIsUploading(true);
      const { data: newArt, error } = await supabase
        .from("art")
        .insert([{ text, recipe, category }])
        .select();
      setIsUploading(false);

      // 4. Add the new art to the UI: add the art to state
      if (!error) setArt((art) => [newArt[0], ...art]);

      // 5. Reset input fields
      setText("");
      setRecipe("");
      setCategory("");

      // 6. Close the form
      setShowForm(false);
    }
  }

  return (
    <form className="art-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Tell us about your latte art..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        disabled={isUploading}
      />
      <span>{200 - textLength}</span>
      <input
        value={recipe}
        type="text"
        placeholder="Trustworthy recipe..."
        onChange={(e) => setRecipe(e.target.value)}
        disabled={isUploading}
      />
      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        disabled={isUploading}
      >
        <option value="">Choose category:</option>
        {CATEGORIES.map((cat) => (
          <option key={cat.name} value={cat.name}>
            {cat.name.toUpperCase()}
          </option>
        ))}
      </select>
      <button className="btn btn-large" disabled={isUploading}>
        Post
      </button>
    </form>
  );
}

function CategoryFilter({ setCurrentCategory }) {
  return (
    <aside>
      <ul>
        <li className="category">
          <button
            className="btn btn-all-categories"
            onClick={() => setCurrentCategory("all")}
          >
            All
          </button>
        </li>

        {CATEGORIES.map((cat) => (
          <li key={cat.name} className="category">
            <button
              className="btn btn-category"
              style={{ backgroundColor: cat.color }}
              onClick={() => setCurrentCategory(cat.name)}
            >
              {cat.name}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function ArtList({ art, setArt }) {
  if (art.length === 0)
    return (
      <p className="message">
        No art for this category yet! Create the first one ‚úåÔ∏è
      </p>
    );

  return (
    <section>
      <ul className="art-list">
        {art.map((art) => (
          <art key={art.id} art={art} setArt={setArt} />
        ))}
      </ul>
      <p>There are {art.length} art in the database. Add your own!</p>
    </section>
  );
}

function Art({ art, setArt }) {
  const [isUpdating, setIsUpdating] = useState(false);
  const isDisputed =
    art.votesInteresting + art.votesMindblowing < art.votesFalse;

  async function handleVote(columnName) {
    setIsUpdating(true);
    const { data: updatedArt, error } = await supabase
      .from("art")
      .update({ [columnName]: art[columnName] + 1 })
      .eq("id", art.id)
      .select();
    setIsUpdating(false);

    if (!error)
      setArt((art) => art.map((f) => (f.id === art.id ? updatedArt[0] : f)));
  }

  return (
    <li className="art">
      <p>
        {isDisputed ? <span className="disputed">[‚õîÔ∏è DISPUTED]</span> : null}
        {art.text}
        <a className="recipe" href={art.recipe} target="_blank">
          (recipe)
        </a>
      </p>
      <span
        className="tag"
        style={{
          backgroundColor: CATEGORIES.find((cat) => cat.name === art.category)
            .color,
        }}
      >
        {art.category}
      </span>
      <div className="vote-buttons">
        <button onClick={() => handleVote("votesMug")} disabled={isUpdating}>
          üëç {art.votesMug}
        </button>
        <button onClick={() => handleVote("votesHeart")} disabled={isUpdating}>
          ü§Ø {art.votesHeart}
        </button>
        <button
          onClick={() => handleVote("votesMindBlown")}
          disabled={isUpdating}
        >
          ‚õîÔ∏è {art.votesMindBlown}
        </button>
      </div>
    </li>
  );
}

export default App;
