import { useState } from "react";
import "./style.css";

const initialArt = [
  {
    id: 1,
    text: "This is my latte art. I made a flower.",
    recipe: "https://opensource.fb.com/",
    category: "espresso",
    votesMug: 24,
    votesHeart: 9,
    votesMindBlown: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "I SUCK at latte art.",
    recipe:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "lattes",
    votesMug: 17,
    votesHeart: 9,
    votesMindBlown: 1,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Cortados are my favorite milk drink.",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "cortados",
    votesMug: 8,
    votesHeart: 2,
    votesMindBlown: 1,
    createdIn: 2015,
  },
];

function Counter() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <span style={{ fontSize: "40px" }}>{count}</span>
      <button className="btn btn-large" onClick={() => setCount((c) => c + 1)}>
        +1
      </button>
    </div>
  );
}

function App() {
  const [showForm, setShowForm] = useState(false);

  const appTitle = "Today's Latte Art";

  return (
    <>
      {/* HEADER */}
      <header className="header">
        <div className="logo">
          <img
            src="logo.png"
            height="68"
            width="68"
            alt="Today's Latte Art Logo"
          />
          <h1>{appTitle}</h1>
        </div>
        <button
          className="btn btn-large btn-post"
          onClick={() => setShowForm((show) => !show)}
        >
          Share a picture
        </button>
      </header>

      {showForm ? <NewArtForm /> : null}

      <main className="main">
        <CategoryFilter />
        <ArtList />
      </main>
    </>
  );
}

function NewArtForm() {
  return <form className="art-form">Art form</form>;
}

const CATEGORIES = [
  { name: "espresso", color: "#3b82f6" },
  { name: "lattes", color: "#16a34a" },
  { name: "cortados", color: "#ef4444" },
  { name: "cappucinos", color: "#eab308" },
];

function CategoryFilter() {
  return (
    <aside>
      <ul>
        <li className="category">
          <button className="btn btn-all-categories">All</button>
        </li>
        {CATEGORIES.map((cat) => (
          <li key={cat.name} className="category">
            <button
              className="btn btn-category"
              style={{ backgroundColor: cat.color }}
            >
              {cat.name}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function ArtList() {
  const art = initialArt;

  return (
    <section>
      <ul className="art-list">
        {art.map((art) => (
          <Art key={art.id} art={art} />
        ))}
      </ul>
      <p>There are {art.length} posts in the database. Add your own!</p>
    </section>
  );
}

function Art({ art }) {
  return (
    <li className="art">
      <p>
        {art.text}
        <a className="recipe" href={art.source} target="_blank">
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{
          backgroundColor: CATEGORIES.find((cat) => cat.name === art.category)
            .color,
        }}
      >
        {art.category}
      </span>
      <div className="vote-buttons">
        <button>‚òïÔ∏è {art.votesMug}</button>
        <button>‚ù§Ô∏è {art.votesHeart}</button>
        <button>ü§Ø {art.votesMindBlown}</button>
      </div>
    </li>
  );
}

export default App;
