import "./style.css";

const CATEGORIES = [
  { name: "espresso", color: "#3b82f6" },
  { name: "lattes", color: "#16a34a" },
  { name: "cortados", color: "#ef4444" },
  { name: "cappucinos", color: "#eab308" },
];

function App() {
  const appTitle = "Today's Latte Art";

  return (
    <>
      {/* HEADER */}
      <header className="header">
        <div className="logo">
          <img
            src="logo.png"
            height="68"
            width="68"
            alt="Today's Latte Art Logo"
          />
          <h1>{appTitle}</h1>
        </div>
        <button className="btn btn-large btn-post">Share a picture</button>
      </header>

      <NewArtForm />
      <main className="main">
        <CategoryFilter />
        <ArtList />
      </main>
    </>
  );
}

function NewArtForm() {
  return <form className="art-form">Art form</form>;
}

function CategoryFilter() {
  return <aside>Category filter</aside>;
}

function ArtList() {
  const art = initialArt;

  return <section>Art list</section>;
}

export default App;
