import { useEffect, useState } from "react";
import supabase from "./supabase";
import "./style.css";

const initialArt = [
  {
    id: 1,
    text: "This is my latte art. I made a flower.",
    recipe: "https://opensource.fb.com/",
    category: "espresso",
    votesMug: 24,
    votesHeart: 9,
    votesMindBlown: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "I SUCK at latte art.",
    recipe:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "lattes",
    votesMug: 17,
    votesHeart: 9,
    votesMindBlown: 1,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Cortados are my favorite milk drink.",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "cortados",
    votesMug: 8,
    votesHeart: 2,
    votesMindBlown: 1,
    createdIn: 2015,
  },
];

function App() {
  const [showForm, setShowForm] = useState(false);
  const [art, setArt] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(function () {
    async function getArt() {
      setIsLoading(true);
      const { data: art, error } = await supabase
        .from("art")
        .select("*")
        .order("votesMug", { ascending: false })
        .limit(1);
      setArt(art);
      setIsLoading(false);
    }
    getArt();
  }, []);

  function Loader() {
    return <p className="message">Loading...</p>;
  }

  return (
    <>
      <Header showForm={showForm} setShowForm={setShowForm} />

      {showForm ? (
        <NewArtForm setArt={setArt} setShowForm={setShowForm} />
      ) : null}

      <main className="main">
        <CategoryFilter />
        {isLoading ? <Loader /> : <ArtList art={art} />}
      </main>
    </>
  );
}

function Header({ showForm, setShowForm }) {
  const appTitle = "Today's Latte Art";

  return (
    <header className="header">
      <div className="logo">
        <img
          src="logo.png"
          height="68"
          width="68"
          alt="Today's Latte Art Logo"
        />
        <h1>{appTitle}</h1>
      </div>
      <button
        className="btn btn-large btn-post"
        onClick={() => setShowForm((show) => !show)}
      >
        {showForm ? "Close" : "Share a picture"}
      </button>
    </header>
  );
}

const CATEGORIES = [
  { name: "espresso", color: "#3b82f6" },
  { name: "lattes", color: "#16a34a" },
  { name: "cortados", color: "#ef4444" },
  { name: "cappucinos", color: "#eab308" },
];

function isValidHttpUrl(string) {
  let url;
  try {
    url = new URL(string);
  } catch (_) {
    return false;
  }
  return url.protocol === "http:" || url.protocol === "https:";
}

function NewArtForm({ setArt, setShowForm }) {
  const [text, setText] = useState("");
  const [recipe, setRecipe] = useState("");
  const [category, setCategory] = useState("");
  const textLength = text.length;

  function handleSubmit(e) {
    // 1. Prevent browser reload
    e.preventDefault();
    // 2. Check if data is valid. If so, create a new art
    if (text && isValidHttpUrl(recipe) && category && text.length <= 200)
      console.log("there is valid data");
    // 3. Create a new art object
    const newArt = {
      id: Math.round(Math.random() * 100000),
      text,
      recipe,
      category,
      votesMug: 0,
      votesHeart: 0,
      votesMindBlown: 0,
      createdIn: new Date().getFullYear(),
    };
    // 4. Add new art to the UI

    setArt((art) => [newArt, ...art]);
    // 5. Reset input fields
    setText("");
    setRecipe("");
    setCategory("");
    // 6. Close the form
    setShowForm(false);
  }

  return (
    <form className="art-form" onSubmit={handleSubmit}>
      {" "}
      <input
        type="text"
        placeholder="Tell us about your latte art..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <span>{200 - textLength}</span>
      <input
        type="text"
        placeholder="Recipe link..."
        value={recipe}
        onChange={(e) => setRecipe(e.target.value)}
      />
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option value="">Choose category:</option>
        {CATEGORIES.map((cat) => (
          <option key={cat.name} value={cat.name}>
            {cat.name.toUpperCase()}
          </option>
        ))}
      </select>
      <button className="btn btn-large">Post</button>
    </form>
  );
}

function CategoryFilter() {
  return (
    <aside>
      <ul>
        <li className="category">
          <button className="btn btn-all-categories">All</button>
        </li>
        {CATEGORIES.map((cat) => (
          <li key={cat.name} className="category">
            <button
              className="btn btn-category"
              style={{ backgroundColor: cat.color }}
            >
              {cat.name}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function ArtList({ art }) {
  return (
    <section>
      <ul className="art-list">
        {art.map((art) => (
          <Art key={art.id} art={art} />
        ))}
      </ul>
      <p>There are {art.length} posts in the database. Add your own!</p>
    </section>
  );
}

function Art({ art }) {
  return (
    <li className="art">
      <p>
        {art.text}
        <a className="recipe" href={art.source} target="_blank">
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{
          backgroundColor: CATEGORIES.find((cat) => cat.name === art.category)
            .color,
        }}
      >
        {art.category}
      </span>
      <div className="vote-buttons">
        <button>☕️ {art.votesMug}</button>
        <button>❤️ {art.votesHeart}</button>
        <button>🤯 {art.votesMindBlown}</button>
      </div>
    </li>
  );
}

export default App;
